import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import {MenuItem,Select,Radio,FormControl,FormLabel,RadioGroup,Grid,Accordion,Typography,Paper,Box,Link,Checkbox,TextField,Button,FormControlLabel,AccordionSummary,Slider, Divider} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Logo from './assets/images/imagen_reportes.png';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { withStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
	color:'#438EB9'
  },
  image: {
    backgroundImage: `url(${Logo})`,
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[20] : theme.palette.grey[700],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },

  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
	backgroundColor:'#438EB9'
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0,
  },
  colorFondo:{
	backgroundColor:'#438EB9'
  }, 
  colorPrimary:{
	color:'#438EB9'
  }, 
  colorLetra:{
	color:'#438EB9'
  }, 
  colorBorder:{
	backgroundBorder:'#438EB9'
  },
  accordion:{
  paddingLeft: '14px',
  paddingBottom: '14px',
  border: '2px #438EB9 solid'
  }

}));


export default function App() {
  const classes = useStyles();

  const [patologiaGrado, setPatologiaGrado] = React.useState("patologia_grado_ninguno");
  const [patologiaEnfermedad, setPatologiaEnfermedad] = React.useState("patologia_enfermedad_ninguno");
  const [patologiaValvular, setPatologiaValular] = React.useState("patologia_valvular_ninguno");
  const [feyVI, setFeyVI] = React.useState("ninguno");
  const [edad, setEdad] = React.useState([1, 100]);
  const [sexo, setSexo] = React.useState("femenino");
  const AzulRadio = withStyles({
	root: {
	  color: '#438EB9',
	  '&$checked': {
		color: '#438EB9',
	  },
	},
	checked: {},
  })((props) => <Radio color="default" {...props} />);

  const handleChangeEdad = (e, data) => {

    setEdad(data);
  };
  const handleChangeSexo = (e, data) => {
    setSexo(data);
	e.stopPropagation()
  };
  const handleChangeFeyVi = (e, data) => {
    setFeyVI(data);
  };
  const handleChangePatologiaValular = (e, data) => {
    setPatologiaValular(data);
  };
  const handleChangePatologiaEnfermedad = (e, data) => {
    setPatologiaEnfermedad(data);
  };
  const handleChangePatologiaGrado = (e, data) => {
    setPatologiaGrado(data);
  };
  function valuetext(value) {
	return value;
  }
  const Paciente = (props) => {
		return ( 

		<Accordion  className={classes.accordion}> 
		<AccordionSummary
			expandIcon={<ExpandMoreIcon />}
			aria-controls="pacientebh-content"
			id="pacientebh-header"
		>
	  <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Paciente</strong></Typography>

	</AccordionSummary>
	<FormLabel  component="legend" style={{paddingLeft:'20px'}}>Seleccione un rango de edad </FormLabel>

	<Grid container spacing={2}>
		<Grid item md={4} >
		<Slider
			className={classes.colorPrimary}
			min={1}
			max={100}
			value={edad}
			onChange={(event,value)=>{ event.stopPropagation();setEdad(value); }}

			valueLabelDisplay="auto"
			aria-labelledby="range-slider"
			getAriaValueText={valuetext}
			valueLabelDisplay="on"

			onFocus={(event) => event.stopPropagation()}
      />
		</Grid>
		<Grid item md={8} ></Grid>
	 	<Grid item md={12} >
		 <FormControl component="fieldset">
      <FormLabel component="legend">Sexo</FormLabel>
		<RadioGroup aria-label="gender" name="sexo" onFocus={(event) => event.stopPropagation()} value={sexo} onChange={handleChangeSexo}>
			<FormControlLabel  value="femenino"  control={<AzulRadio  />} label="Femenino" />
			<FormControlLabel value="masculino"   control={<AzulRadio />} label="Masculino" />
			<FormControlLabel value="ambos"  control={<AzulRadio />} label="Ambos" />

		</RadioGroup>
    </FormControl>
		</Grid> 
		<Grid item md={4} >
	<FormLabel component="legend">Fecha de nacimiento </FormLabel>

			<TextField
				id="date"

				type="date"
				defaultValue="2017-05-24"
				name="fecha_nacimiento"
				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid> 
		<Grid item md={4} >
		<FormLabel component="legend">Fecha de ingreso </FormLabel>

		<TextField
				id="date"
				type="date"
				defaultValue="2017-05-24"
				name="fecha_ingreso"

				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid>
		<Grid item md={4} ></Grid>
	</Grid>

	  </Accordion>)
  }

  const FactoresRiesgo = (props)=>{
	  return(
		<Accordion className={classes.accordion}> 
			<AccordionSummary
				expandIcon={<ExpandMoreIcon />}
				aria-controls="factores_de_riesgobh-content"
				id="factores_de_riesgobh-header"
			>
	  		<Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Factores de riesgo</strong></Typography>

			</AccordionSummary>
			<FormControlLabel
				control={<Checkbox  value="fr[HTA]"  />}
				label="HTA"
			/>
			<FormControlLabel

				onFocus={(event) => event.stopPropagation()}
				control={<Checkbox value="fr[DLP]"  />}
				label="DLP"
			/>
			<FormControlLabel

				control={<Checkbox value="fr[DBT]"  />}
				label="DBT"
			/>
				<FormControlLabel

				control={<Checkbox value="fr[DBT IR]"  />}
				label="DBT IR"
			/>
			<FormControlLabel

				control={<Checkbox value="fr[TBQ]"  />}
				label="TBQ"
			/>
			<FormControlLabel

				control={<Checkbox value="fr[Ex TBQ]"  />}
				label="Ex TBQ"
			/>
			<FormControlLabel

				control={<Checkbox value="fr[AHF]"  />}
				label="AHF"
			/>
			<FormControlLabel

				control={<Checkbox value="fr[OBESIDAD]"  />}
				label="OBESIDAD"
			/>
	  </Accordion>
	  )
  }
  const Antecedentes = (props)=>{
	  const menor35 = "< 35%";
	  const mayor55 = "> 55%";
	return(
	  <Accordion className={classes.accordion}> 
	  	<AccordionSummary
		  expandIcon={<ExpandMoreIcon />}
		  aria-controls="antecedentesbh-content"
		  id="antecedentesbh-header"
	  	>
			<Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Antecedentes</strong></Typography>

		</AccordionSummary>
		<Grid container spacing={2}>
		<Grid item md={4} >
			<FormControlLabel
			control={<Checkbox  value="ant[EPOC]"  />}
			label=" Enfermedad Pulmonar Obstructiva Crónica (EPOC)"
			/>
			<FormControlLabel
			control={<Checkbox value="ant[IRC]"  />}
			label="Insuficiencia Renal Crónica (IRC)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[CRM]"  />}
			label="Cirugía de Revascularización Miocárdica (CRM)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[ATC]"  />}
			label="Angioplastia Transluminal Coronaria (ATC)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[IAM]"  />}
			label="nfarto Agudo de Miocardio (IAM)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[ACV]"  />}
			label="Ataque Cerebrovascular (ACV)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[AIT]"  />}
			label="Ataque isquémico transitorio (AIT)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[FA]"  />}
			label="Fibrilación Auricular (FA)"
			/>
				<FormControlLabel
