import React from 'react';
import useForm from './customHooks';
import {MenuItem,Select,Radio,FormControl,FormLabel,RadioGroup,Grid,Accordion,Typography,Paper,Box,Link,Checkbox,TextField,Button,FormControlLabel,AccordionSummary,Slider, Divider} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import Logo from '../assets/images/imagen_reportes.png';
import { DatePicker,KeyboardDatePicker,MuiPickersUtilsProvider} from "@material-ui/pickers";
import DateFnsUtils from '@date-io/date-fns';

const useStyles = makeStyles((theme) => ({
	root: {
	  height: '100vh',
	  color:'#438EB9'
	},
	image: {
	  backgroundImage: `url(${Logo})`,
	  backgroundRepeat: 'no-repeat',
	  backgroundColor:
		theme.palette.type === 'light' ? theme.palette.grey[20] : theme.palette.grey[700],
	  backgroundSize: 'cover',
	  backgroundPosition: 'center',
	},
	paper: {
	  margin: theme.spacing(8, 4),
	  display: 'flex',
	  flexDirection: 'column',
	  alignItems: 'center',
	},

	form: {
	  width: '100%', // Fix IE 11 issue.
	  marginTop: theme.spacing(1),
	},
	submit: {
	  margin: theme.spacing(3, 0, 2),
	  backgroundColor:'#438EB9'
	},
	heading: {
	  fontSize: theme.typography.pxToRem(15),
	  flexBasis: '33.33%',
	  flexShrink: 0,
	},
	colorFondo:{
	  backgroundColor:'#438EB9'
	}, 
	colorPrimary:{
	  color:'#438EB9'
	}, 
	colorLetra:{
	  color:'#438EB9'
	}, 
	colorBorder:{
	  backgroundBorder:'#438EB9'
	},
	accordion:{
	paddingLeft: '14px',
	paddingBottom: '14px',
	border: '2px #438EB9 solid'
	}

  }));

export default function  Form () {
	const classes = useStyles();


    const AzulRadio = withStyles({
		root: {
		  color: '#438EB9',
		  '&$checked': {
			color: '#438EB9',
		  },
		},
		checked: {},
	  })((props) => <Radio color="default" {...props} />);

    const { 
		handleSubmit,
		handleInputChange,
		handleChangeAccordion,
		handleChangeEdad,
		valuetext,
		inputs,
		expanded,
		paciente_fecha_nac,
		paciente_fecha_creacion,
		paciente_edad,
		localeMap,
		locale} = useForm({
		paciente_sexo:"0",
		paciente:true,
		antecedentes:false,
		factoresRiesgos:false,
		clinica:false,
		datosAngio:false,
		lesiones:false,
		procedimiento:false,
		complicacionesSala:false

      });

	  const Paciente = (props) => {
		return ( 

		<Accordion  className={classes.accordion} expanded={expanded.paciente}  onChange={handleChangeAccordion("paciente")}> 
		<AccordionSummary
			expandIcon={<ExpandMoreIcon />}
			aria-controls="pacientebh-content"
			id="pacientebh-header"
		>
	  <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Paciente</strong></Typography>

	</AccordionSummary>
	<FormLabel  component="legend" style={{paddingLeft:'20px'}}>Seleccione un rango de edad </FormLabel>

	<Grid container spacing={2}>
		<Grid item md={4} >
		<Slider
			className={classes.colorPrimary}
			min={1}
			max={100}
			value={paciente_edad}
			onChange={handleChangeEdad}
			valueLabelDisplay="auto"
			aria-labelledby="range-slider"
			getAriaValueText={valuetext}
			valueLabelDisplay="on"

      />

		</Grid>
		<Grid item md={8} ></Grid>
	 	<Grid item md={12} >
		 <FormControl component="fieldset">
      <FormLabel component="legend">Sexo</FormLabel>
		<RadioGroup row={true} aria-label="gender" name="paciente_sexo" onFocus={(event) => event.stopPropagation()} value={inputs.paciente_sexo} onChange={handleInputChange}>
			<FormControlLabel  value={"0"}  
             control={<AzulRadio  />} label="Femenino" />
			<FormControlLabel value={"1"}   onClick={(event) => { event.stopPropagation();
    event.nativeEvent.stopImmediatePropagation();}}
              control={<AzulRadio />} label="Masculino" />
			<FormControlLabel value={"2"}  onClick={(event) => { event.stopPropagation();
    event.nativeEvent.stopImmediatePropagation();}}
             control={<AzulRadio />} label="Ambos" />

		</RadioGroup>
    </FormControl>
		</Grid> 
		<Grid item md={4} >
			<FormLabel component="legend">Fecha de nacimiento </FormLabel>

			<DatePicker
				disableFuture
				openTo="year"
				format="dd/MM/yyyy"
				views={["year", "month", "date"]}
				value={paciente_fecha_nac}
				onChange={date => handleDateChange(date)}
			/>

		</Grid> 
		<Grid item md={4} >
		<FormLabel component="legend">Fecha de ingreso </FormLabel>
		<DatePicker
				disableFuture
				openTo="year"
				format="dd/MM/yyyy"	
				views={["year", "month", "date"]}
				value={paciente_fecha_creacion}
				onChange={date => handleDateChange(date)}
			/>

		</Grid>
		<Grid item md={4} ></Grid>
	</Grid>

	  </Accordion>)
  }

    return (
      <form onSubmit={handleSubmit}>
	       <Grid container component="main" className={classes.root}>
		   		<Grid item xs={false} sm={4} md={5} className={classes.image} />
     		 		<Grid item xs={12} sm={8} md={7} component={Paper} elevation={6} square>
					  <MuiPickersUtilsProvider utils={DateFnsUtils} locale={localeMap[locale]}>
							<Paciente />
							<Accordion  className={classes.accordion} expanded={expanded.antecedentes}  onChange={handleChangeAccordion("antecedentes")}> 
							<AccordionSummary
							expandIcon={<ExpandMoreIcon />}
							aria-controls="pacientebh-content"
							id="pacientebh-header"
							>
							<Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>antecedentes</strong></Typography>

							</AccordionSummary>
							</Accordion>
							</MuiPickersUtilsProvider>
					</Grid>
				</Grid>



	      <button type="submit">Crear reporte</button>
	    </form>
    )
}
