import React from 'react';
import useForm from './customHooks';
import {MenuItem,Select,Radio,FormControl,FormLabel,RadioGroup,Grid,Accordion,Typography,Paper,Box,Link,Checkbox,TextField,Button,FormControlLabel,AccordionSummary,Slider, Divider} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';

export default function  Form () {
	const classes = useStyles();

    const AzulRadio = withStyles({
		root: {
		  color: '#438EB9',
		  '&$checked': {
			color: '#438EB9',
		  },
		},
		checked: {},
	  })((props) => <Radio color="default" {...props} />);

    const {inputs, handleInputChange, handleSubmit} = useForm({
		paciente_edad:"",
		paciente_sexo:"0",
		paciente_fecha_nac:null,
		paciente:true,
		antecedentes:false,
		factoresRiesgos:false,
		clinica:false,
		datosAngio:false,
		lesiones:false,
		procedimiento:false,
		complicacionesSala:false

      });
	  const Paciente = (props) => {
		return ( 

		<Accordion  className={classes.accordion} expanded={inputs.paciente} onChange={handleInputChange('paciente')}> 
		<AccordionSummary
			expandIcon={<ExpandMoreIcon />}
			aria-controls="pacientebh-content"
			id="pacientebh-header"
		>
	  <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Paciente</strong></Typography>

	</AccordionSummary>
	<FormLabel  component="legend" style={{paddingLeft:'20px'}}>Seleccione un rango de edad </FormLabel>

	<Grid container spacing={2}>
		<Grid item md={4} >
		<Slider
			className={classes.colorPrimary}
			min={1}
			max={100}
			value={inputs.paciente_edad}
			onChange={handleInputChange}
			valueLabelDisplay="auto"
			aria-labelledby="range-slider"
			getAriaValueText={inputs.paciente_edad}
			valueLabelDisplay="on"
			name="paciente_edad"

      />
		</Grid>
		<Grid item md={8} ></Grid>
	 	<Grid item md={12} >
		 <FormControl component="fieldset">
      <FormLabel component="legend">Sexo</FormLabel>
		<RadioGroup row={true} aria-label="gender" name="paciente_sexo" onFocus={(event) => event.stopPropagation()} value={inputs.paciente_sexo} onChange={handleInputChange}>
			<FormControlLabel  value={"0"}  
             control={<AzulRadio  />} label="Femenino" />
			<FormControlLabel value={"1"}   onClick={(event) => { event.stopPropagation();
    event.nativeEvent.stopImmediatePropagation();}}
              control={<AzulRadio />} label="Masculino" />
			<FormControlLabel value={"2"}  onClick={(event) => { event.stopPropagation();
    event.nativeEvent.stopImmediatePropagation();}}
             control={<AzulRadio />} label="Ambos" />

		</RadioGroup>
    </FormControl>
		</Grid> 
		<Grid item md={4} >
	<FormLabel component="legend">Fecha de nacimiento </FormLabel>

			<TextField
				id="date"

				type="date"
				defaultValue="2017-05-24"
				name="paciente_fecha_nac"
				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid> 
		<Grid item md={4} >
		<FormLabel component="legend">Fecha de ingreso </FormLabel>

		<TextField
				id="date"
				type="date"
				defaultValue="2017-05-24"
				name="paciente[fecha_creacion]"

				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid>
		<Grid item md={4} ></Grid>
	</Grid>

	  </Accordion>)
  }

    return (
      <form onSubmit={handleSubmit}>
	       <Grid container component="main" className={classes.root}>
	        <label>Email Address</label>
	        <input type="email" name="paciente_edad" onChange={handleInputChange} value={inputs.paciente_edad} required />
			</Grid>

		<Paciente />
	      <button type="submit">Submit</button>
	    </form>
    )
}
