import React,{useState} from 'react';
import enLocale from "date-fns/locale/es";

export default function useForm  (initialValues){
    const [inputs,setInputs] = useState(initialValues);
    const [paciente_edad, setPaciente_edad] = React.useState([1, 100]);
	const [paciente_fecha_nac, handleDateChange] = React.useState(null);
	const [paciente_fecha_creacion,handleDateFCChange] = React.useState(null);
    const [expanded, setExpanded] = React.useState({
      paciente:true,
      antecedentes:false,
      factoresRiesgos:false,
      clinica:false,
      datosAngio:false,
      lesiones:false,
      procedimiento:false,
      complicacionesSala:false
      });
      const [localeMap] = React.useState({
        es:enLocale
      })
      const [locale] = React.useState("es");	
      function valuetext(value) {
        return value;
        }
        const handleChangeEdad = (e,data) => {

        setPaciente_edad(data);
        e.preventDefault()
        };
    const handleSubmit = (event) => {
      console.log(inputs)


		let paciente = {};
		let antecedentes={};
		let factoresRiesgos = {};
		let form = {};
		paciente = {
			edad:paciente_edad,
			sexo:inputs.paciente_sexo,
			fecha_nac:paciente_fecha_nac,
			fecha_creacion:paciente_fecha_creacion
		}
		antecedentes = {
			antecedente_ACV: inputs.antecedente_ACV,
			antecedente_AIT: inputs.antecedente_AIT,
			antecedente_ATC: inputs.antecedente_ATC,
			antecedente_CRM: inputs.antecedente_CRM,
			antecedente_EPOC: inputs.antecedente_EPOC,
			antecedente_FA: inputs.antecedente_FA,
			antecedente_HTP: inputs.antecedente_HTP,
			antecedente_IAM: inputs.antecedente_IAM,
			antecedente_IRC: inputs.antecedente_IRC,
			antecedente_MCPH: inputs.antecedente_MCPH,
			antecedente_feyVI: inputs.antecedente_feyVI,
			antecedente_patologia_enfermedad:inputs.antecedente_patologia_enfermedad,
			antecedente_patologia_grado: inputs.antecedente_patologia_grado,
			antecedente_patologia_valvular: inputs.antecedente_patologia_valvular
		}

		factoresRiesgos = {
			factor_riesgo_AHF: inputs.factor_riesgo_AHF,
			factor_riesgo_DBT: inputs.factor_riesgo_DBT,
			factor_riesgo_DBT_IR: inputs.factor_riesgo_DBT_IR,
			factor_riesgo_DLP: inputs.factor_riesgo_DLP,
			factor_riesgo_Ex_TBQ: inputs.factor_riesgo_Ex_TBQ,
			factor_riesgo_HTA: inputs.factor_riesgo_HTA,
			factor_riesgo_OBESIDAD: inputs.factor_riesgo_OBESIDAD,
			factor_riesgo_TBQ: inputs.factor_riesgo_TBQ,
		}

		form ={
			paciente:paciente,
			antecedentes:antecedentes,
			factores_riesgo:factoresRiesgos

		}

		console.log(form)
      if(event){
        event.preventDefault();
      }
    }
    const handleChangeAccordion = (panel)=>(event, newExpanded) => {

      setExpanded({...expanded,[panel]:newExpanded})
    };

    const handleInputChange = (event) => {
      console.log("sssssss",event.target.name)
      console.log("sssssss",event.target.value)
      setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
      }
     const handleInputCheckedChange = (event) => {
		console.log("sssssss",event.target.name)
		console.log("sssssss",event.target.value)
		console.log("sssssss",event.target.checked)
		setInputs(inputs => ({...inputs, [event.target.name]: event.target.checked}));
		}
    return {
      handleSubmit,
     handleInputChange,
     handleChangeAccordion,
	 handleChangeEdad,
	 valuetext,
	 handleDateChange,
	 handleDateFCChange,
	 handleInputCheckedChange,
     inputs,
     expanded,
     paciente_fecha_nac,
     paciente_fecha_creacion,
     paciente_edad,
     localeMap,
     locale
      };
}
