import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import {Radio,FormControl,FormLabel,RadioGroup,Grid,Accordion,Typography,Paper,Box,Link,Checkbox,TextField,Button,FormControlLabel,AccordionSummary,Slider} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Logo from './assets/images/imagen_reportes.png';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: `url(${Logo})`,
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[20] : theme.palette.grey[700],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },

  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
	backgroundColor:'#438EB9'
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0,
  },
  colorFondo:{
	backgroundColor:'#438EB9'
  }, 
  colorLetra:{
	color:'#438EB9'
  }, 
  colorBorder:{
	backgroundBorder:'#438EB9'
  }
}));

export default function App() {
  const classes = useStyles();
  const [expandedAccordion, setExpandedAccordion] = React.useState(true);
  const [edad, setEdad] = React.useState([1, 100]);
