import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import {MenuItem,Select,Radio,FormControl,FormLabel,RadioGroup,Grid,Accordion,Typography,Paper,Box,Link,Checkbox,TextField,Button,FormControlLabel,AccordionSummary,Slider, Divider} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import Logo from './assets/images/imagen_reportes.png';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: `url(${Logo})`,
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[20] : theme.palette.grey[700],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },

  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
	backgroundColor:'#438EB9'
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0,
  },
  colorFondo:{
	backgroundColor:'#438EB9'
  }, 
  colorLetra:{
	color:'#438EB9'
  }, 
  colorBorder:{
	backgroundBorder:'#438EB9'
  }
}));

export default function App() {
  const classes = useStyles();

  const [feyVI, setFeyVI] = React.useState("ninguno");
  const [edad, setEdad] = React.useState([1, 100]);
  const [sexo, setSexo] = React.useState("femenino");


  const handleChangeEdad = (e, data) => {
    setEdad(data);
  };
  const handleChangeSexo = (e, data) => {
    setSexo(data);
  };
  const handleChangeFeyVi = (e, data) => {
    setFeyVI(data);
  };
  function valuetext(value) {
	return value;
  }
  const Paciente = (props) => {
		return ( 

		<Accordion > 
		<AccordionSummary
			expandIcon={<ExpandMoreIcon />}
			aria-controls="pacientebh-content"
			id="pacientebh-header"
		>
	  <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Paciente</strong></Typography>

	</AccordionSummary>
	<FormLabel  component="legend">Seleccione un rango de edad </FormLabel>

	<Grid container spacing={2}>
		<Grid item md={4} >
		<Slider
			value={edad}
			onChange={handleChangeEdad}
			valueLabelDisplay="auto"
			aria-labelledby="range-slider"
			getAriaValueText={valuetext}
      />
		</Grid>
		<Grid item md={8} ></Grid>
	 	<Grid item md={12} >
		 <FormControl component="fieldset">
      <FormLabel component="legend">Sexo</FormLabel>
		<RadioGroup aria-label="gender" name="sexo" value={sexo} onChange={handleChangeSexo}>
			<FormControlLabel value="femenino" control={<Radio />} label="Femenino" />
			<FormControlLabel value="masculino" control={<Radio />} label="Masculino" />
			<FormControlLabel value="ambos" control={<Radio />} label="Ambos" />

		</RadioGroup>
    </FormControl>
		</Grid> 
		<Grid item md={4} >
	<FormLabel component="legend">Fecha de nacimiento </FormLabel>

			<TextField
				id="date"

				type="date"
				defaultValue="2017-05-24"
				name="fecha_nacimiento"
				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid> 
		<Grid item md={4} >
		<FormLabel component="legend">Fecha de ingreso </FormLabel>

		<TextField
				id="date"
				type="date"
				defaultValue="2017-05-24"
				name="fecha_ingreso"

				InputLabelProps={{
				shrink: true,
				}}
			/>
		</Grid>
		<Grid item md={4} ></Grid>
	</Grid>

	  </Accordion>)
  }

  const FactoresRiesgo = (props)=>{
	  return(
		<Accordion > 
		<AccordionSummary
			expandIcon={<ExpandMoreIcon />}
			aria-controls="factores_de_riesgobh-content"
			id="factores_de_riesgobh-header"
		>
	  <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Factores de riesgo</strong></Typography>

			</AccordionSummary>
		<FormControlLabel
			control={<Checkbox  value="fr[HTA]"  />}
			label="HTA"
			/>
			<FormControlLabel
			control={<Checkbox value="fr[DLP]"  />}
			label="DLP"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[DBT]"  />}
			label="DBT"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[DBT IR]"  />}
			label="DBT IR"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[TBQ]"  />}
			label="TBQ"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[Ex TBQ]"  />}
			label="Ex TBQ"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[AHF]"  />}
			label="AHF"
			/>
				<FormControlLabel
			control={<Checkbox value="fr[OBESIDAD]"  />}
			label="OBESIDAD"
			/>
	  </Accordion>
	  )
  }
  const Antecedentes = (props)=>{
	  const menor35 = "< 35%";
	  const mayor55 = "> 55%";
	return(
	  <Accordion > 
	  	<AccordionSummary
		  expandIcon={<ExpandMoreIcon />}
		  aria-controls="antecedentesbh-content"
		  id="antecedentesbh-header"
	  	>
			<Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Antecedentes</strong></Typography>

		</AccordionSummary>
		<Grid container spacing={2}>
		<Grid item md={4} >
			<FormControlLabel
			control={<Checkbox  value="ant[EPOC]"  />}
			label=" Enfermedad Pulmonar Obstructiva Crónica (EPOC)"
			/>
			<FormControlLabel
			control={<Checkbox value="ant[IRC]"  />}
			label="Insuficiencia Renal Crónica (IRC)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[CRM]"  />}
			label="Cirugía de Revascularización Miocárdica (CRM)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[ATC]"  />}
			label="Angioplastia Transluminal Coronaria (ATC)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[IAM]"  />}
			label="nfarto Agudo de Miocardio (IAM)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[ACV]"  />}
			label="Ataque Cerebrovascular (ACV)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[AIT]"  />}
			label="Ataque isquémico transitorio (AIT)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[FA]"  />}
			label="Fibrilación Auricular (FA)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[HTP]"  />}
			label="Hipertensión Pulmonar (HTP)"
			/>
				<FormControlLabel
			control={<Checkbox value="ant[MCPH]"  />}
			label="Miocardiopatía Hipertrófica (MCPH)"
			/>
		  </Grid>
		  <Grid item md={8} >
		  <Grid container  >
		  <Grid item md={12} >
			<FormLabel component="legend">FEy-VI</FormLabel>

			<Select

			id="demo-simple-select"
			value={feyVI}
			onChange={handleChangeFeyVi}
			name="feyvi"
			>
			<MenuItem value={"ninguno"}>Ninguno</MenuItem>
			<MenuItem value={"< 35%"}> {menor35}</MenuItem>
			<MenuItem value={"35 a 45%"}>35 a 45%</MenuItem>
			<MenuItem value={"45 a 55%"}>45 a 55%</MenuItem>
			<MenuItem value={"> 55%"}>{mayor55}</MenuItem>
			</Select>
			</Grid>
			<Grid item md={12} >
			<Divider variant="middle" className={classes.colorFondo} />
			</Grid>
			<Grid item md={12} >
				<FormLabel component="legend"><strong>Patologia Valular</strong></FormLabel>
				<FormLabel component="legend">Valvula</FormLabel>

			<Select

			id="demo-simple-select"
			value={feyVI}
			onChange={handleChangeFeyVi}
			name="feyvi"
			>
			<MenuItem value={"patologia_valvular_ninguno"}>Ninguno</MenuItem>
			<MenuItem value={"patologia_valvular_1"}> Aórtica </MenuItem>
			<MenuItem value={"patologia_valvular_2"}>Mitral</MenuItem>
			<MenuItem value={"patologia_valvular_3"}>tricuspiedea </MenuItem>
			<MenuItem value={"patologia_valvular_4"}>Pulmonar </MenuItem>

			</Select>
			</Grid>
		</Grid>
		  </Grid>
		  </Grid>
	</Accordion>
	)
}
  return (
    <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={5} className={classes.image} />
      <Grid item xs={12} sm={8} md={7} component={Paper} elevation={6} square>
        <div className={classes.paper}>

          <Typography component="h1" variant="h5">
           Formulario para sacar reportes
          </Typography>
          <form className={classes.form} noValidate>
		 <Paciente />
		 <Antecedentes/>
		<FactoresRiesgo/>
		  <Accordion > 
			<AccordionSummary
				expandIcon={<ExpandMoreIcon />}
				aria-controls="clinicabh-content"
				id="clinicabh-header"
			>
          <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Clinica</strong></Typography>

       		 </AccordionSummary>
			<FormControlLabel
				control={<Checkbox value="remember" color="primary" />}
				label="Remember me"
				/>
		  </Accordion>
		  <Accordion  > 
			<AccordionSummary
				expandIcon={<ExpandMoreIcon />}
				aria-controls="datos_angiograficosbh-content"
				id="datos_angiograficosbh-header"
			>
          <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Datos angiograficos</strong></Typography>

       		 </AccordionSummary>
			<FormControlLabel
				control={<Checkbox value="remember" color="primary" />}
				label="Remember me"
				/>
		  </Accordion>
		  <Accordion > 
			<AccordionSummary
				expandIcon={<ExpandMoreIcon />}
				aria-controls="lesionesbh-content"
				id="lesionesbh-header"
			>
          <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Lesiones</strong></Typography>

       		 </AccordionSummary>
			<FormControlLabel
				control={<Checkbox value="remember" color="primary" />}
				label="Remember me"
				/>
		  </Accordion>
		  <Accordion > 
			<AccordionSummary
				expandIcon={<ExpandMoreIcon />}
				aria-controls="procedimientobh-content"
				id="procedimientobh-header"
			>
          <Typography className={`${classes.heading} ${classes.colorLetra}`}><strong>Procedimiento</strong></Typography>

       		 </AccordionSummary>
			<FormControlLabel
				control={<Checkbox value="remember" color="primary" />}
				label="Remember me"
				/>
		  </Accordion>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
             Consultar
            </Button>


          </form>
        </div>
      </Grid>
    </Grid>
  );
}
